import React, { useState } from 'react';

function HomeScreen({ onCreateRoom, onJoinRoom, initialRoomCode }) {
  const [mode, setMode] = useState('home');
  const [playerName, setPlayerName] = useState('');
  const [roomCode, setRoomCode] = useState(initialRoomCode || '');

  const handleCreate = () => {
    if (playerName.trim()) {
      onCreateRoom(playerName);
    }
  };

  const handleJoin = () => {
    if (playerName.trim() && roomCode.trim()) {
      onJoinRoom(playerName, roomCode.toUpperCase());
    }
  };

  if (mode === 'home') {
    return (
      <div className="screen">
        <h1>ðŸŽ® Sequence Game</h1>
        <p className="subtitle">Multiplayer Card Strategy Game</p>
        <div className="button-group">
          <button onClick={() => setMode('create')}>Create Room</button>
          <button onClick={() => setMode('join')}>Join Room</button>
        </div>
      </div>
    );
  }

  if (mode === 'create') {
    return (
      <div className="screen">
        <h1>Create Game Room</h1>
        <input
          type="text"
          placeholder="Enter your name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleCreate()}
        />
        <div className="button-group">
          <button onClick={handleCreate}>Create Room</button>
          <button onClick={() => setMode('home')}>Back</button>
        </div>
      </div>
    );
  }

  return (
    <div className="screen">
      <h1>Join Game Room</h1>
      <input
        type="text"
        placeholder="Enter your name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Enter room code"
        value={roomCode}
        onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
        onKeyPress={(e) => e.key === 'Enter' && handleJoin()}
      />
      <div className="button-group">
        <button onClick={handleJoin}>Join Room</button>
        <button onClick={() => setMode('home')}>Back</button>
      </div>
    </div>
  );
}

export default HomeScreen;
