import React from 'react';

function Board({ board, onCellClick, selectedCard }) {
  return (
    <div className="game-board">
      {board.map((row, r) => (
        row.map((cell, c) => {
          const isCorner = cell.isCorner;
          const hasChip = cell.chip !== null;
          const isClickable = !isCorner && !hasChip && selectedCard;

          return (
            <div
              key={`${r}-${c}`}
              className={`board-cell ${isCorner ? 'corner' : ''} ${hasChip ? 'has-chip' : ''} ${isClickable ? 'clickable' : ''}`}
              onClick={() => isClickable && onCellClick(r, c)}
            >
              {isCorner ? '★' : cell.card}
              {hasChip && <div className={`chip ${cell.chip}`}></div>}
            </div>
          );
        })
      ))}
    </div>
  );
}

export default Board;
