import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import HomeScreen from './components/HomeScreen';
import LobbyScreen from './components/LobbyScreen';
import GameScreen from './components/GameScreen';
import './App.css';

const SOCKET_URL = 'http://localhost:5000';

function App() {
  const [socket, setSocket] = useState(null);
  const [screen, setScreen] = useState('home');
  const [roomCode, setRoomCode] = useState('');
  const [player, setPlayer] = useState(null);
  const [players, setPlayers] = useState([]);
  const [gameState, setGameState] = useState(null);
  const [teamMode, setTeamMode] = useState(false);

  useEffect(() => {
    const newSocket = io(SOCKET_URL);
    setSocket(newSocket);

    // Check URL for room code
    const urlParams = new URLSearchParams(window.location.search);
    const urlRoomCode = urlParams.get('room');
    if (urlRoomCode) {
      setRoomCode(urlRoomCode);
    }

    return () => newSocket.close();
  }, []);

  useEffect(() => {
    if (!socket) return;

    socket.on('room_created', (data) => {
      setRoomCode(data.roomCode);
      setPlayer(data.player);
      setPlayers([data.player]);
      setScreen('lobby');
    });

    socket.on('room_joined', (data) => {
      setRoomCode(data.roomCode);
      setPlayer(data.player);
      setPlayers(data.players);
      setScreen('lobby');
    });

    socket.on('players_updated', (data) => {
      setPlayers(data.players);
    });

    socket.on('game_settings_updated', (data) => {
      setTeamMode(data.teamMode);
      setPlayers(data.players);
    });

    socket.on('player_left', (data) => {
      setPlayers(prev => prev.filter(p => p.id !== data.playerId));
    });

    socket.on('game_started', (data) => {
      setGameState(data);
      setScreen('game');
    });

    socket.on('game_updated', (data) => {
      setGameState(prev => ({
        ...prev,
        board: data.board,
        currentTurnIndex: data.currentTurnIndex,
        sequences: data.sequences,
        winner: data.winner
      }));
    });

    socket.on('hand_updated', (data) => {
      setGameState(prev => ({
        ...prev,
        hand: data.hand
      }));
    });

    socket.on('error', (data) => {
      alert(data.message);
    });

    return () => {
      socket.off('room_created');
      socket.off('room_joined');
      socket.off('players_updated');
      socket.off('game_settings_updated');
      socket.off('player_left');
      socket.off('game_started');
      socket.off('game_updated');
      socket.off('hand_updated');
      socket.off('error');
    };
  }, [socket]);

  const createRoom = (playerName) => {
    socket.emit('create_room', { playerName });
  };

  const joinRoom = (playerName, code) => {
    socket.emit('join_room', { playerName, roomCode: code });
  };

  const toggleTeamMode = (enabled) => {
    socket.emit('toggle_team_mode', { roomCode, teamMode: enabled });
  };

  const startGame = () => {
    socket.emit('start_game', { roomCode });
  };

  const playCard = (card, row, col) => {
    socket.emit('play_card', { roomCode, card, row, col });
  };

  const leaveRoom = () => {
    socket.emit('leave_room', { roomCode });
    setScreen('home');
    setRoomCode('');
    setPlayer(null);
    setPlayers([]);
    setGameState(null);
  };

  return (
    <div className="App">
      {screen === 'home' && (
        <HomeScreen 
          onCreateRoom={createRoom}
          onJoinRoom={joinRoom}
          initialRoomCode={roomCode}
        />
      )}

      {screen === 'lobby' && (
        <LobbyScreen
          roomCode={roomCode}
          players={players}
          player={player}
          teamMode={teamMode}
          onToggleTeamMode={toggleTeamMode}
          onStartGame={startGame}
          onLeaveRoom={leaveRoom}
        />
      )}

      {screen === 'game' && gameState && (
        <GameScreen
          gameState={gameState}
          player={player}
          onPlayCard={playCard}
          onLeaveGame={leaveRoom}
        />
      )}
    </div>
  );
}

export default App;
